INSERT INTO public.arabic_data (title, content, category, tags, metadata) VALUES
  ('الذكاء الاصطناعي والمستقبل', 'الذكاء الاصطناعي يشكل مستقبل التكنولوجيا ويؤثر على جميع جوانب حياتنا اليومية. من التعلم الآلي إلى معالجة اللغة الطبيعية، تتطور التقنيات بسرعة هائلة.', 'technology', ARRAY['AI', 'تكنولوجيا', 'مستقبل'], '{"author": "نظام البيانات", "views": 1250}'::jsonb),
  
  ('اكتشافات علمية حديثة', 'العلماء يكتشفون يوميا أسرارا جديدة عن الكون والطبيعة. هذه الاكتشافات تساعدنا على فهم أفضل للعالم من حولنا وتفتح آفاقا جديدة للبحث والتطوير.', 'science', ARRAY['علوم', 'اكتشافات', 'بحث'], '{"author": "فريق البحث", "views": 890}'::jsonb),
  
  ('الشعر العربي الكلاسيكي', 'الشعر العربي له تاريخ طويل وغني يمتد لآلاف السنين. من المعلقات السبع إلى شعر العصر الحديث، يعكس الشعر العربي ثقافة وحضارة غنية.', 'literature', ARRAY['شعر', 'أدب', 'ثقافة'], '{"author": "باحث أدبي", "views": 2100}'::jsonb),
  
  ('تاريخ الحضارة الإسلامية', 'الحضارة الإسلامية ساهمت بشكل كبير في تطور العلوم والفنون والفلسفة. من بغداد إلى قرطبة، كانت المدن الإسلامية مراكز للمعرفة والتعليم.', 'history', ARRAY['تاريخ', 'حضارة', 'إسلام'], '{"author": "مؤرخ", "views": 1560}'::jsonb),
  
  ('أساليب التعليم الحديثة', 'التعليم يتطور باستمرار مع ظهور تقنيات جديدة. التعلم عن بعد والفصول الافتراضية أصبحت جزءا أساسيا من منظومة التعليم الحديث.', 'education', ARRAY['تعليم', 'تقنية', 'تطوير'], '{"author": "مدرس", "views": 750}'::jsonb),
  
  ('ريادة الأعمال في العالم العربي', 'ريادة الأعمال تنمو بشكل ملحوظ في المنطقة العربية. الشركات الناشئة تلعب دورا محوريا في تنويع الاقتصاد وخلق فرص عمل جديدة.', 'business', ARRAY['أعمال', 'ريادة', 'اقتصاد'], '{"author": "رائد أعمال", "views": 1890}'::jsonb),
  
  ('برمجة تطبيقات الويب', 'تطوير تطبيقات الويب يتطلب معرفة بلغات البرمجة الحديثة مثل JavaScript و Python. الأطر الحديثة مثل React و Next.js تسهل عملية التطوير.', 'technology', ARRAY['برمجة', 'ويب', 'تطوير'], '{"author": "مبرمج", "views": 3200}'::jsonb),
  
  ('الفيزياء الكمومية', 'الفيزياء الكمومية تكشف عن عالم غريب على المستوى الذري. من التشابك الكمومي إلى مبدأ عدم اليقين، تتحدى هذه النظريات فهمنا للواقع.', 'science', ARRAY['فيزياء', 'كم', 'علوم'], '{"author": "فيزيائي", "views": 1420}'::jsonb),
  
  ('الرواية العربية المعاصرة', 'الرواية العربية المعاصرة تعكس قضايا المجتمع الحديث وتتناول مواضيع متنوعة من السياسة إلى الهوية والانتماء.', 'literature', ARRAY['رواية', 'أدب', 'معاصر'], '{"author": "ناقد أدبي", "views": 980}'::jsonb),
  
  ('التطور التاريخي للعمارة الإسلامية', 'العمارة الإسلامية تتميز بتصاميمها الفريدة والزخارف الهندسية المعقدة. من قبة الصخرة إلى الحمراء، تحف معمارية خالدة.', 'history', ARRAY['عمارة', 'تاريخ', 'فن'], '{"author": "معماري", "views": 1670}'::jsonb);
